package bitmanipulations;

public class bitwise_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A = 10, B = 12;
		System.out.println(A & B);
		System.out.println(A | B);
		System.out.println(~A + " and " + ~B);
		System.out.println(A ^ B);
	}

}
